import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Container, Nav } from 'react-bootstrap';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const CustomNavbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status
  const [isAdmin, setIsAdmin] = useState(false); // State to track admin status
  const navigate = useNavigate();

  // Function to check if the user is logged in and if they are an admin
  const checkLoggedInStatus = () => {
    const token = localStorage.getItem('token');
    const adminToken = localStorage.getItem('adminToken');
    const isAdmin = !!adminToken; // Check if admin token exists
    setIsLoggedIn(!!token);
    setIsAdmin(isAdmin);
  };

  useEffect(() => {
    checkLoggedInStatus();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    localStorage.removeItem('Name');
    localStorage.removeItem('token');
    localStorage.removeItem('adminToken');
    setIsLoggedIn(false); // Set isLoggedIn to false upon logout
    setIsAdmin(false); // Set isAdmin to false upon logout
    toast.success('Logout successful!');
    navigate('/login');
  };

  return (
    <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">Food Rage</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {isLoggedIn && !isAdmin ? (
              <>
                <Nav.Link as={Link} to="/orders">My Orders</Nav.Link>
                <Nav.Link as={Link} to="/cart">Cart</Nav.Link>
                <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
              </>
            ) : isLoggedIn && isAdmin ? (
              <>
                <Nav.Link as={Link} to="/admin">Admin Dashboard</Nav.Link>
                <Nav.Link onClick={handleLogout}>Logout Admin</Nav.Link>
              </>
            ) : (
              <Nav.Link as={Link} to="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomNavbar;
