import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Row, Col, Button, DropdownButton, Dropdown } from 'react-bootstrap';

export default function Home() {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cart, setCart] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const foodResponse = await axios.get('http://localhost:5000/foods');
        setFoods(foodResponse.data);

        const categoryResponse = await axios.get('http://localhost:5000/category');
        setCategories(categoryResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = (food) => {
    // Assuming food object contains _id, name, price, and options array
    const quantity = 1; // You can implement quantity selection logic if needed
    const cartItem = { name: food.name, quantity, price: food.price, options: food.options };
    setCart([...cart, cartItem]);
  };

  return (
    <div>
      <h1>Categories</h1>
      {categories.map(category => (
        <div key={category._id}>
          <button onClick={() => handleCategoryChange(category.category)}>
            {category.category} ({foods.filter(food => food.category === category.category).length})
          </button>
        </div>
      ))}

      <h1>Foods</h1>
      <Row xs={1} md={3} className="g-4">
        {foods.filter(food => !selectedCategory || food.category === selectedCategory).map(food => (
          <Col key={food._id}>
            <Card>
              <Card.Img variant="top" src={food.image} />
              <Card.Body>
                <Card.Title>{food.name}</Card.Title>
                <Card.Text>
                  <strong>Description:</strong> {food.description}<br />
                  <strong>Price:</strong> ${food.price}<br />
                  <DropdownButton id="dropdown-basic-button" title="Options">
                    {food.options.map(option => (
                      <Dropdown.Item key={option.name}>{option.name}</Dropdown.Item>
                    ))}
                  </DropdownButton>
                </Card.Text>
                <Button variant="primary" onClick={() => handleAddToCart(food)}>Add to Cart</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </div>
  )
}
