import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Modal, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const MyProfile = () => {
  const [user, setUser] = useState(null);
  const [ordersCount, setOrdersCount] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProfileData() {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/user-profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setUser(response.data.user);
        setOrdersCount(response.data.ordersCount);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    }
    
    fetchProfileData();
  }, []);

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('profileImage', profileImage);

      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:5000/user-profile/picture', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log(response.data.message);
      setUser({ ...user, profileImage: response.data.user.profilePicture }); // Update user with new profile picture
      setShowModal(false);
    } catch (error) {
      console.error('Error uploading profile picture:', error);
    }
  };

  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  return (
    <div>
      <h1>My Profile</h1>
      {user ? (
        <Card style={{ width: '18rem' }}>
          <Card.Body>
            <Card.Title>{user.name}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">{user.email}</Card.Subtitle>
            <Card.Text>Orders made: {ordersCount}</Card.Text>
            <img
              src={user.profileImage || 'default-profile-icon.png'}
              alt="Profile"
              style={{ width: '50px', height: '50px', borderRadius: '50%', cursor: 'pointer' }}
              onClick={() => setShowModal(true)}
            />
          </Card.Body>
        </Card>
      ) : (
        <p>Loading...</p>
      )}

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Upload Profile Picture</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Choose Profile Picture</Form.Label>
            <Form.Control type="file" onChange={handleProfileImageChange} />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleUpload}>Upload</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default MyProfile;
