import React, { useState, useEffect } from 'react';
import { CNavbar, CContainer, CNavbarBrand, CNavbarNav, CNavLink, CCollapse, CToggler } from '@coreui/react';
import { toast } from 'react-toastify';
import { useNavigate, Link } from 'react-router-dom';

const CustomNavbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true); // Add loading state
  const [isOpen, setIsOpen] = useState(false); // Add isOpen state
  const navigate = useNavigate();

  useEffect(() => {
    const checkUserLoggedInStatus = () => {
      const userToken = localStorage.getItem('token');
      setIsLoggedIn(!!userToken);
    };

    const checkAdminLoggedInStatus = () => {
      const adminToken = localStorage.getItem('adminToken');
      setIsAdminLoggedIn(!!adminToken);
    };

    checkUserLoggedInStatus();
    checkAdminLoggedInStatus();
    setLoading(false); // Set loading to false once authentication status is checked
  }, []);

  const handleUserLogout = () => {
    localStorage.removeItem('userEmail');
    localStorage.removeItem('Name');
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    toast.success('User logout successful!');
    navigate('/login');
  };

  const handleAdminLogout = () => {
    localStorage.removeItem('adminToken');
    setIsAdminLoggedIn(false);
    toast.success('Admin logout successful!');
    navigate('/admin-login');
  };

  if (loading) return null; // Return null while loading

  return (
    <CNavbar color="light" expandable="md">
      <CContainer fluid>
        <CNavbarBrand to="/">Food Rage</CNavbarBrand>
        <CToggler inNavbar onClick={() => setIsOpen(!isOpen)} />
        <CCollapse show={isOpen} navbar>
          <CNavbarNav>
            {isLoggedIn && !isAdminLoggedIn && (
              <>
                <CNavLink as={Link} to="/orders">My Orders</CNavLink>
                <CNavLink as={Link} to="/cart">Cart</CNavLink>
                <CNavLink onClick={handleUserLogout}>Logout</CNavLink>
              </>
            )}
            {isAdminLoggedIn && (
              <>
                <CNavLink as={Link} to="/admin-dashboard">Admin Dashboard</CNavLink>
                <CNavLink as={Link} to="/food-list">Food List</CNavLink>
                <CNavLink as={Link} to="/user-activity">User Activity</CNavLink>
                <CNavLink onClick={handleAdminLogout}>Logout</CNavLink>
              </>
            )}
            {!isLoggedIn && !isAdminLoggedIn && (
              <>
                <CNavLink as={Link} to="/login">User Login</CNavLink>
                <CNavLink as={Link} to="/admin-login">Admin Login</CNavLink>
              </>
            )}
          </CNavbarNav>
        </CCollapse>
      </CContainer>
    </CNavbar>
  );
};

export default CustomNavbar;
