import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';

export default function Cart({ cart, updateCartItem, removeCartItem }) {
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    // Calculate total price when cart items change
    let totalPrice = 0;
    if (cart) {
      cart.forEach(item => {
        totalPrice += item.price * item.quantity;
      });
    }
    setTotalPrice(totalPrice);
  }, [cart]);

  // Function to handle quantity change for a cart item
  const handleQuantityChange = (index, quantity) => {
    updateCartItem(index, quantity);
  };

  // Function to handle removal of a cart item
  const handleRemoveItem = (index) => {
    removeCartItem(index);
  };

  return (
    <div>
      <h1>Cart</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cart && cart.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>${item.price}</td>
              <td>
                <input
                  type="number"
                  value={item.quantity}
                  onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                  min="1"
                />
              </td>
              <td>
                <Button variant="danger" onClick={() => handleRemoveItem(index)}>Remove</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h2>Total Price: ${totalPrice}</h2>
    </div>
  );
}
