// Cart.js
import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { useCart, useDispatchCart } from './Contextreducer';
import axios from 'axios';

export default function Cart() {
  const cart = useCart();
  const dispatch = useDispatchCart();
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    let totalPrice = 0;
    if (cart) {
      cart.forEach(item => {
        totalPrice += item.price;
      });
    }
    setTotalPrice(totalPrice);
  }, [cart]);

  const handleRemoveItem = (index) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: index });
  };

  const handleCheckout = async () => {
    try {
      // Send a POST request to the backend for checkout
      const response = await axios.post('http://localhost:5000/checkout', { cartItems: cart });
      console.log('Checkout response:', response.data);
      // If checkout successful, clear the cart
      dispatch({ type: 'CLEAR_CART' });
      // Display success message
      alert('Checkout successful!');
    } catch (error) {
      console.error('Checkout error:', error.message);
      // Display error message
      alert('Checkout failed. Please try again later.');
    }
  };

  return (
    <div>
      <h1>Cart</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cart && cart.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>${item.price}</td>
              <td>{item.quantity}</td>
              <td>
                <Button variant="danger" onClick={() => handleRemoveItem(index)}>Remove</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h2>Total Price: ${totalPrice}</h2>
      <Button variant="primary" onClick={handleCheckout}>Checkout</Button>
    </div>
  );
}
