// Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Row, Col, Button, DropdownButton, Dropdown, Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useDispatchCart } from './Contextreducer';

export default function Home() {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedFood, setSelectedFood] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedOption, setSelectedOption] = useState(null); // Newly added state for selected option
  const [modalShow, setModalShow] = useState(false);
  const navigate = useNavigate();
  const dispatch = useDispatchCart();

  useEffect(() => {
    async function fetchData() {
      try {
        const foodResponse = await axios.get('http://localhost:5000/foods');
        setFoods(foodResponse.data);
  
        const categoryResponse = await axios.get('http://localhost:5000/category');
        setCategories(categoryResponse.data.map(category => ({ ...category, count: foodResponse.data.filter(food => food.category === category.category).length })));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
  
    fetchData();
  }, []); 

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = () => {
    if (selectedFood) {
      const price = selectedOption ? selectedOption.price : selectedFood.price; // Consider option price if selected
      const totalPrice = price * quantity;
      const cartItem = { name: selectedFood.name, quantity, price: totalPrice, option: selectedOption }; // Include selected option in cart item
      dispatch({ type: 'ADD_TO_CART', payload: cartItem });
      setModalShow(false);
      navigate('/cart');
    }
  };

  const handleModalOpen = (food) => {
    setSelectedFood(food);
    setModalShow(true);
    setQuantity(1);
    setSelectedOption(null); // Reset selected option when modal opens
  };

  const handleQuantityChange = (value) => {
    setQuantity(value);
  };
  
  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    if (option) {
      const totalPrice = option.price * quantity;
      setTotalPrice(totalPrice);
    }
  };

  return (
    <div className="bg-dark text-light p-4">
      <h1>Categories</h1>
      <Row>
        {categories.map(category => (
          <Col key={category._id} xs={2} md={2} className="mb-3" onClick={() => handleCategoryChange(category.category)}>
            <div className="bg-secondary text-light p-3 mb-2">
              <h2>{category.category} ({category.count})</h2>
            </div>
          </Col>
        ))}
      </Row>

      <Row>
        {foods.map(food => (
          <Col key={food._id} xs={12} md={4} className="mb-3">
            <Card className="bg-secondary text-light" onClick={() => handleModalOpen(food)}>
              <Card.Img variant="top" src={food.image} />
              <Card.Body>
                <Card.Title>{food.name}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={modalShow} onHide={() => setModalShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedFood && selectedFood.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={selectedFood && selectedFood.image} alt={selectedFood && selectedFood.name} style={{ width: '100%', marginBottom: '10px' }} />
          <p><strong>Price:</strong> ${selectedOption ? selectedOption.price : (selectedFood && selectedFood.price)}</p>
          <p>
            <strong>Quantity:</strong> 
            <DropdownButton id="dropdown-quantity" title={quantity}>
              {[...Array(10).keys()].map((value, index) => (
                <Dropdown.Item key={index} onClick={() => handleQuantityChange(value + 1)}>{value + 1}</Dropdown.Item>
              ))}
            </DropdownButton>
          </p>
          {selectedFood && selectedFood.options && selectedFood.options.length > 0 && (
            <p>
              <strong>Options:</strong>
              <DropdownButton id="dropdown-options" title="Select Option">
                {selectedFood.options.map((option, index) => (
                  <Dropdown.Item key={index} onClick={() => handleOptionSelect(option)}>{option.name} - ${option.price}</Dropdown.Item>
                ))}
              </DropdownButton>
            </p>
          )}
          <p><strong>Total Price:</strong> ${totalPrice}</p> {/* Display total price here */}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setModalShow(false)}>Close</Button>
          <Button variant="primary" onClick={handleAddToCart}>Add to Cart</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
