import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Container, Nav } from 'react-bootstrap';

const CustomNavbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status

  // Simulated function to check if the user is logged in
  const checkLoggedInStatus = () => {
   
    const token = localStorage.getItem('token'); // Example: Retrieve authentication token from localStorage
    setIsLoggedIn(!!token); // Set isLoggedIn to true if token exists, otherwise false
  };

  useEffect(() => {
    checkLoggedInStatus(); // Check login status when component mounts
  }, []);
  const handleLogout = () => {
    localStorage.removeItem('userEmail'); // Remove email from localStorage
    localStorage.removeItem('token'); // Remove token from localStorage
    toast.success('Logout successful!');
    // Optionally redirect to the login page or any other page after logout
    navigate('/login');
  };


  return (
    <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">Food Rage</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/orders">My Orders</Nav.Link>
            {isLoggedIn ? (
              <>
                <Nav.Link as={Link} to="/profile">My Profile</Nav.Link>
                <Nav.Link as={Link} to="/cart">Cart</Nav.Link>
                <Nav.Link as={Link} to="/logout" onClick={handleLogout}>Logout</Nav.Link>
              </>
            ) : (
              <Nav.Link as={Link} to="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomNavbar;
