import React, { useState, useEffect } from "react";
import { Dropdown, Card, Image, Button, Modal, Form } from "react-bootstrap";
import axios from 'axios';

export default function AdminDashboard() {
  const [foods, setFoods] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [showAddFoodModal, setShowAddFoodModal] = useState(false);
  const [newFood, setNewFood] = useState({ name: "", description: "", price: 0, image: "" });

  useEffect(() => {
    const fetchFoods = async () => {
      try {
        const response = await axios.get("http://localhost:5000/foods");
        setFoods(response.data);
      } catch (error) {
        console.error("Error fetching foods:", error);
      }
    };

    fetchFoods();
  }, []);

  const handleOptionSelect = (foodId, option) => {
    setSelectedOptions(prevState => ({
      ...prevState,
      [foodId]: option
    }));
  };

  const handleShowAddFoodModal = () => {
    setShowAddFoodModal(true);
  };

  const handleCloseAddFoodModal = () => {
    setShowAddFoodModal(false);
  };

  const handleAddFood = async () => {
    try {
      await axios.post("http://localhost:5000/foods/add", newFood);
      setShowAddFoodModal(false);
      setNewFood({ name: "", description: "", price: 0, image: "" }); // Reset form fields
    } catch (error) {
      console.error("Error adding food:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFood(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <Button variant="primary" onClick={handleShowAddFoodModal}>Add Food</Button>
      <div className="dashboard-container" style={{ display: "flex", flexWrap: "wrap" }}>
        {foods.map((food) => (
          <Card key={food._id} style={{ width: "250px", margin: "10px" }}>
            <div style={{ width: "250px", height: "250px", overflow: "hidden" }}>
              <Image src={food.image} style={{ width: "100%", height: "100%", objectFit: "cover" }} fluid />
            </div>
            <Card.Body>
              <Card.Title>{food.name}</Card.Title>
              <Card.Text>{food.description}</Card.Text>
              <Card.Text>Price: ${selectedOptions[food._id]?.price || 0}</Card.Text>
              <Dropdown>
                <Dropdown.Toggle variant="primary" id="dropdown-basic">
                  Options
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  {food.options.map((option) => (
                    <Dropdown.Item 
                      key={option._id} 
                      onClick={() => handleOptionSelect(food._id, option)}
                    >
                      {option.name} - ${option.price}
                    </Dropdown.Item>
                  ))}
                </Dropdown.Menu>
              </Dropdown>
              <Button variant="primary" onClick={() => console.log("Edit food")}>Edit</Button>
            </Card.Body>
          </Card>
        ))}
      </div>

      {/* Add Food Modal */}
      <Modal show={showAddFoodModal} onHide={handleCloseAddFoodModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Food</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="foodName">
              <Form.Label>Food Name</Form.Label>
              <Form.Control type="text" placeholder="Enter food name" name="name" value={newFood.name} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" rows={3} placeholder="Enter description" name="description" value={newFood.description} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" placeholder="Enter price" name="price" value={newFood.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" placeholder="Enter image URL" name="image" value={newFood.image} onChange={handleInputChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseAddFoodModal}>Cancel</Button>
          <Button variant="primary" onClick={handleAddFood}>Add Food</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
