import React, { useEffect, useState } from "react";
import { Table, Button, Modal, Form } from "react-bootstrap";
import axios from "axios";
import { loadStripe } from '@stripe/stripe-js';


const stripePromise = loadStripe('pk_test_51OsrdwSCJqXT6DmsHaWW7tPZOBOxtk00mAiK8O1PsQ2FF7RPhyeRxxGgVlxOhTVsHIBjhlfwLsgbGrE34dwfje3i00oqJcWEVF');

export default function Orders() {
  const [orders, setOrders] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [stripe, setStripe] = useState(null);
  const [cardElement, setCardElement] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const userEmail = localStorage.getItem("userEmail");
        const userName = localStorage.getItem("Name");

        if (!userEmail || !userName) {
          console.error("User email or name not found in localStorage");
          return;
        }

        const response = await axios.get("http://localhost:5000/orders", {
          headers: {
            userEmail,
            userName,
          },
        });

        const userOrders = response.data.filter(
          (order) => order.email === userEmail && order.Name === userName
        );
        const totalPrice = userOrders.reduce(
          (acc, order) => acc + totalOrderPrice(order.cartItems),
          0
        );

        setOrders(userOrders);
        setTotalPrice(totalPrice);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    fetchOrders();
  }, []);

  useEffect(() => {
    const initializeStripe = async () => {
      const stripeInstance = await stripePromise;
      setStripe(stripeInstance);

      // Create an instance of Elements
      const elements = stripeInstance.elements();

      // Create a card element
      const card = elements.create('card');
      setCardElement(card);
    };

    initializeStripe();
  }, []);

  const totalOrderPrice = (cartItems) => {
    return cartItems.reduce((acc, item) => {
      const itemPrice = item.option ? item.option.price : item.price;
      return acc + itemPrice * item.quantity;
    }, 0);
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleDeleteOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:5000/orders/${orderId}`);
      const updatedOrders = orders.filter((order) => order._id !== orderId);
      const totalPriceAfterDelete = updatedOrders.reduce(
        (acc, order) => acc + totalOrderPrice(order.cartItems),
        0
      );

      setOrders(updatedOrders);
      setTotalPrice(totalPriceAfterDelete);

      alert("Order deleted successfully!");
    } catch (error) {
      console.error("Error deleting order:", error);
      alert("Failed to delete order. Please try again later.");
    }
  };

  const handlePaymentSuccess = async () => {
    try {
      if (!cardElement) {
        console.error('Card element is not mounted.');
        return;
      }
  
      const userEmail = localStorage.getItem("userEmail");
      const userName = localStorage.getItem("Name");
  
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardElement,
      });
  
      if (error) {
        console.error('Error creating payment method:', error);
        alert("Payment failed. Please try again later.");
        return;
      }
  
      const response = await axios.post(
        "http://localhost:5000/payment/stripe",
        {
          amount: totalPrice * 100, // Amount in cents
          currency: "INR",
          email: userEmail,
          userName,
          paymentMethodId: paymentMethod.id,
        }
      );
  
      if (response.status === 200) {
        const { clientSecret } = response.data;
  
        const result = await stripe.confirmCardPayment(clientSecret, {
          payment_method: paymentMethod.id,
        });
  
        if (result.error) {
          console.error('Error confirming card payment:', result.error);
          alert("Payment failed. Please try again later.");
        } else {
          alert("Payment successful!");
          // Update the UI or redirect to a success page
        }
      } else {
        console.error("Unexpected response:", response);
        alert("Payment failed. Unexpected response from the server.");
      }
    } catch (error) {
      console.error("Error processing payment:", error);
      alert("Payment failed. Please try again later.");
    }
  };
  

  return (
    <div>
      <h1>Orders</h1>
      {orders.length > 0 ? (
        <>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order._id}>
                  <td>{order._id}</td>
                  <td>
                    {order.cartItems.map((item) => (
                      <div key={item._id}>{item.name}</div>
                    ))}
                  </td>
                  <td>
                    {order.cartItems.map((item) => (
                      <div key={item._id}>
                        $
                        {item.option
                          ? item.option.price * item.quantity
                          : item.price * item.quantity}
                      </div>
                    ))}
                  </td>
                  <td>
                    {order.cartItems.map((item) => (
                      <div key={item._id}>{item.quantity}</div>
                    ))}
                  </td>
                  <td>${totalOrderPrice(order.cartItems)}</td>
                  <td>
                    <Button
                      variant="info"
                      onClick={() => handleViewDetails(order)}
                    >
                      View Details
                    </Button>{" "}
                    <Button
                      variant="danger"
                      onClick={() => handleDeleteOrder(order._id)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <h2>Total Price of All Orders: ${totalPrice}</h2>
          <Button variant="success" onClick={() => setShowPaymentModal(true)}>
            Make Payment
          </Button>
        </>
      ) : (
        <p>No orders placed yet.</p>
      )}

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Order Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            <strong>Order ID:</strong> {selectedOrder && selectedOrder._id}
          </p>
          <p>
            <strong>Product Name:</strong>{" "}
            {selectedOrder && selectedOrder.cartItems[0].name}
          </p>
          <p>
            <strong>Price:</strong> $
            {selectedOrder && totalOrderPrice(selectedOrder.cartItems)}
          </p>
          <p>
            <strong>Quantity:</strong>{" "}
            {selectedOrder &&
              selectedOrder.cartItems.reduce(
                (total, item) => total + item.quantity,
                0
              )}
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showPaymentModal} onHide={() => setShowPaymentModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Make Payment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="paymentMethod">
              <Form.Label>Select Payment Method</Form.Label>
              <Form.Control
                as="select"
                value="stripe" // Only Stripe is supported in this version
                disabled
              >
                <option value="stripe">Stripe</option>
              </Form.Control>
            </Form.Group>
            <div id="card-element">
              {/* Card element will be mounted here */}
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowPaymentModal(false)}
          >
            Cancel
          </Button>
          <Button variant="primary" onClick={handlePaymentSuccess}>
            Pay ${totalPrice}
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
