import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import axios from 'axios';

export default function Orders() {
  const [orders, setOrders] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const handleDeleteOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:5000/orders/${orderId}`);
      // Remove the deleted order from the state
      setOrders(orders.filter(order => order._id !== orderId));
      alert('Order deleted successfully!');
    } catch (error) {
      console.error('Error deleting order:', error);
      alert('Failed to delete order. Please try again later.');
    }
  };
  
  useEffect(() => {
    async function fetchOrders() {
      try {
        const response = await axios.get('http://localhost:5000/orders');
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    }
  
    fetchOrders();
  }, []);
  
  useEffect(() => {
    const totalPrice = orders.reduce((acc, order) => {
      const orderTotal = order.cartItems.reduce((subtotal, item) => subtotal + item.price * item.quantity, 0);
      return acc + orderTotal;
    }, 0);
    setTotalPrice(totalPrice);
  }, [orders]);
  
  

  return (
    <div>
      <h1>Orders</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>User</th>
            <th>Total Price</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order._id}>
              <td>{order._id}</td>
              <td>{order.email}</td>
              <td>${totalOrderPrice(order.cartItems)}</td>
              <td>
                <Button variant="info">View Details</Button>
              </td>
              <td>
                <Button variant="danger" onClick={()=>{handleDeleteOrder(order._id)}}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h2>Total Price of All Orders: ${totalPrice}</h2>
    </div>
  );
}

// Helper function to calculate total price of an order
function totalOrderPrice(cartItems) {
  return cartItems.reduce((acc, item) => {
    const itemPrice = item.option ? item.option.price : item.price;
    return acc + itemPrice * item.quantity;
  }, 0);
}
