import React, { useState, useEffect } from "react";
import { Dropdown, Card, Button, Modal, Form } from "react-bootstrap";
import axios from 'axios';

export default function AdminDashboard() {
  const [foods, setFoods] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [newFood, setNewFood] = useState({ name: "", price: 0, image: "", options: [] });
  const [showAddFoodModal, setShowAddFoodModal] = useState(false);
  
  useEffect(() => {
    fetchFoods();
  }, []);

  const fetchFoods = async () => {
    try {
      const response = await axios.get("http://localhost:5000/foods");
      setFoods(response.data);
    } catch (error) {
      console.error("Error fetching foods:", error);
    }
  };

  const handleOptionSelect = (option) => {
    setSelectedOptions([...selectedOptions, option]);
  };

  const handleAddFood = async () => {
    try {
      const token = localStorage.getItem('token');
      const newFoodWithOptions = {
        ...newFood,
        options: selectedOptions
      };
      await axios.post("http://localhost:5000/foods/add", newFoodWithOptions, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setShowAddFoodModal(false);
      setNewFood({ name: "", price: 0, image: "", options: [] });
      setSelectedOptions([]);
      fetchFoods();
    } catch (error) {
      console.error("Error adding food:", error);
    }
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <Button onClick={() => setShowAddFoodModal(true)}>Add Food</Button>
      <div className="dashboard-container">
        {foods.map((food) => (
          <Card key={food._id}>
            <Card.Body>
              <Card.Title>{food.name}</Card.Title>
              <Card.Text>Price: ${food.price}</Card.Text>
              <Dropdown>
                <Dropdown.Toggle variant="primary" id="dropdown-basic">
                  Options
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  {food.options.map((option) => (
                    <Dropdown.Item 
                      key={option._id} 
                      onClick={() => handleOptionSelect(option)}
                    >
                      {option.name} - ${option.price}
                    </Dropdown.Item>
                  ))}
                </Dropdown.Menu>
              </Dropdown>
            </Card.Body>
          </Card>
        ))}
      </div>
      <Modal show={showAddFoodModal} onHide={() => setShowAddFoodModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Food</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter name" 
                value={newFood.name}
                onChange={(e) => setNewFood({ ...newFood, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control 
                type="number" 
                placeholder="Enter price" 
                value={newFood.price}
                onChange={(e) => setNewFood({ ...newFood, price: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter image URL" 
                value={newFood.image}
                onChange={(e) => setNewFood({ ...newFood, image: e.target.value })}
              />
            </Form.Group>
          </Form>
          <Dropdown>
            <Dropdown.Toggle variant="primary" id="dropdown-basic">
              Add Option
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {selectedOptions.map((option, index) => (
                <Dropdown.Item key={index}>
                  {option.name} - ${option.price}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddFoodModal(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleAddFood}>Add Food</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
