import React, { useState, useEffect } from "react";
import { Dropdown, Card, Image, Button, Modal, Form } from "react-bootstrap";
import axios from 'axios';

export default function AdminDashboard() {
  const [foods, setFoods] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);

  const [showAddFoodModal, setShowAddFoodModal] = useState(false);
  const [editFoodModalVisible, setEditFoodModalVisible] = useState(false);
  const [editedFood, setEditedFood] = useState({}); // New state for edited food item

  const [newFood, setNewFood] = useState({ name: "", price: 0, image: "", options: [] });
  const [newOption, setNewOption] = useState({ name: "", price: 0 });

  useEffect(() => {
    fetchFoods();
  }, []);

  const fetchFoods = async () => {
    try {
      const response = await axios.get("http://localhost:5000/foods");
      setFoods(response.data);
    } catch (error) {
      console.error("Error fetching foods:", error);
    }
  };

  const handleOptionSelect = (foodId, option) => {
    const selectedFood = foods.find(food => food._id === foodId);
    if (!selectedFood) return;
  
    const selectedOption = selectedFood.options.find(opt => opt._id === option._id);
    if (!selectedOption) return;
  
    setSelectedOptions(prevOptions => {
      const updatedOptions = [...prevOptions];
      const existingOptionIndex = updatedOptions.findIndex(opt => opt._id === option._id);
      if (existingOptionIndex !== -1) {
        updatedOptions[existingOptionIndex] = { ...updatedOptions[existingOptionIndex], price: selectedOption.price };
      } else {
        updatedOptions.push({ _id: option._id, name: option.name, price: selectedOption.price });
      }
      return updatedOptions;
    });
  };

  const handleEditFood = (food) => {
    setEditedFood(food);
    setEditFoodModalVisible(true);
  };

  const handleCloseEditFoodModal = () => {
    setEditFoodModalVisible(false);
  };

  const handleEditFoodSubmit = async () => {
    try {
      const adminToken = localStorage.getItem('adminToken');
      if (!adminToken) {
        throw new Error('Admin token not found');
      }
  
      const response = await axios.put(`http://localhost:5000/foods/${editedFood._id}/update`, editedFood, {
        headers: {
          Authorization: `Bearer ${adminToken}`
        }
      });
      console.log(response);
      setEditFoodModalVisible(false);
      fetchFoods();
    } catch (error) {
      console.error("Error editing food:", error);
    }
  };
  

  const handleShowAddFoodModal = () => {
    setShowAddFoodModal(true);
  };

  const handleCloseAddFoodModal = () => {
    setShowAddFoodModal(false);
  };

  const handleAddOption = () => {
    setNewFood(prevState => ({
      ...prevState,
      options: [...prevState.options, { name: newOption.name, price: newOption.price }]
    }));
    setNewOption({ name: "", price: 0 });
  };

  const handleAddFood = async () => {
    try {
      const adminToken = localStorage.getItem('adminToken');
      if (!adminToken) {
        throw new Error('Admin token not found');
      }
  
      const response = await axios.post("http://localhost:5000/foods/add", newFood, {
        headers: {
          Authorization: `Bearer ${adminToken}`
        }
      });
      console.log(response);
      setShowAddFoodModal(false);
      setNewFood({ name: "", price: 0, image: "", options: [] }); // Reset form fields
      fetchFoods(); // Fetch foods again to update the list
    } catch (error) {
      console.error("Error adding food:", error);
    }
  };
  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewFood(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleOptionChange = (e) => {
    const { name, value } = e.target;
    setNewOption(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <Button variant="primary" onClick={handleShowAddFoodModal}>Add Food</Button>
      <div className="dashboard-container" style={{ display: "flex", flexWrap: "wrap" }}>
        {foods.map((food) => (
          <Card key={food._id} style={{ width: "250px", margin: "10px" }}>
            <div style={{ width: "250px", height: "250px", overflow: "hidden" }}>
              <Image src={food.image} style={{ width: "100%", height: "100%", objectFit: "cover" }} fluid />
            </div>
            <Card.Body>
              <Card.Title>{food.name}</Card.Title>
              <Card.Text>Price: ${selectedOptions.find(opt => opt._id === food._id)?.price || food.price}</Card.Text>
              <Dropdown>
                <Dropdown.Toggle variant="primary" id="dropdown-basic">
                  Options
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  {food.options.map((option) => (
                    <Dropdown.Item 
                      key={option._id} 
                      onClick={() => handleOptionSelect(food._id, option)}
                    >
                      {option.name} - ${option.price}
                    </Dropdown.Item>
                  ))}
                </Dropdown.Menu>
              </Dropdown>
              <Button variant="primary" onClick={() => handleEditFood(food)}>Edit</Button>
            </Card.Body>
          </Card>
        ))}
      </div>

      {/* Add Food Modal */}
      <Modal show={showAddFoodModal} onHide={handleCloseAddFoodModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add Food</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="foodName">
              <Form.Label>Food Name</Form.Label>
              <Form.Control type="text" placeholder="Enter food name" name="name" value={newFood.name} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" placeholder="Enter price" name="price" value={newFood.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" placeholder="Enter image URL" name="image" value={newFood.image} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="foodOptions">
              <Form.Label>Options</Form.Label>
              <div style={{ display: "flex", alignItems: "center" }}>
                <Form.Control type="text" placeholder="Option name" name="name" value={newOption.name} onChange={handleOptionChange} />
                <Form.Control type="number" placeholder="Option price" name="price" value={newOption.price} onChange={handleOptionChange} />
                <Button variant="primary" onClick={handleAddOption}>Add Option</Button>
              </div>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseAddFoodModal}>Cancel</Button>
          <Button variant="primary" onClick={handleAddFood}>Add Food</Button>
        </Modal.Footer>
      </Modal>

      {/* Edit Food Modal */}
      <Modal show={editFoodModalVisible} onHide={handleCloseEditFoodModal}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Food</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="editFoodName">
              <Form.Label>Food Name</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter food name" 
                value={editedFood.name} 
                onChange={(e) => setEditedFood({ ...editedFood, name: e.target.value })} 
              />
            </Form.Group>
            <Form.Group controlId="editFoodPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control 
                type="number" 
                placeholder="Enter price" 
                value={editedFood.price} 
                onChange={(e) => setEditedFood({ ...editedFood, price: e.target.value })} 
              />
            </Form.Group>
            <Form.Group controlId="editFoodImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Enter image URL" 
                value={editedFood.image} 
                onChange={(e) => setEditedFood({ ...editedFood, image: e.target.value })} 
              />
            </Form.Group>
            {/* You can add more form fields here for editing other properties of the food item */}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEditFoodModal}>Cancel</Button>
          <Button variant="primary" onClick={handleEditFoodSubmit}>Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
