import React, { useState } from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CustomNavbar from './components/Navbar';
import Home from './components/Home';

import Orders from './components/Orders';
import Cart from './components/Cart';
import Login from './components/Login';
import Signup from './components/Signup';
import AdminLoginForm from './components/AdminLogin'; // Import the AdminLoginForm component
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { CartProvider } from './components/Contextreducer';

function App() {
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false); // State to track admin login status

  return (
    <CartProvider>
      <Router>
        <div className="App">
          <ToastContainer />
          <CustomNavbar />
          {isAdminLoggedIn ? ( // Conditionally render AdminLoginForm or regular routes
            <AdminLoginForm onLogin={() => setIsAdminLoggedIn(true)} />

          ) : (
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/orders" element={<Orders />} />
              <Route path="/cart" element={<Cart />} />
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
            </Routes>
          )}
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
