// Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Modal, DropdownButton, Dropdown, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useDispatchCart } from './Contextreducer';

export default function Home() {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [isFavorite, setIsFavorite] = useState(false); 
  
  const [selectedFood, setSelectedFood] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedOption, setSelectedOption] = useState(null);
  const [modalShow, setModalShow] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();
  const dispatch = useDispatchCart();

  useEffect(() => {
    async function fetchData() {
      try {
        const foodResponse = await axios.get('http://localhost:5000/foods');
        setFoods(foodResponse.data);
  
        const categoryResponse = await axios.get('http://localhost:5000/category');
        const categories = categoryResponse.data.map(category => ({
          ...category,
          count: foodResponse.data.filter(food => food.category === category.category).length
        }));
  
        const favoritesResponse = await axios.get('http://localhost:5000/favorites');
        setFavorites(favoritesResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
  
    fetchData();
  }, []); 
   

  const handleAddToCart = () => {
    if (selectedFood) {
      const price = selectedOption ? selectedOption.price : selectedFood.price;
      const totalPrice = price * quantity;
      const cartItem = { name: selectedFood.name, quantity, price: totalPrice, option: selectedOption };
      dispatch({ type: 'ADD_TO_CART', payload: cartItem });
      setModalShow(false);
      navigate('/cart');
    }
  };

  const handleModalOpen = (food) => {
    setSelectedFood(food);
    setModalShow(true);
    setQuantity(1);
    setSelectedOption(null);
    setTotalPrice(food.price);
  };

  const handleQuantityChange = (value) => {
    setQuantity(value);
    const totalPrice = selectedOption ? selectedOption.price * value : selectedFood.price * value;
    setTotalPrice(totalPrice);
  };
  
  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    if (option) {
      const totalPrice = option.price * quantity;
      setTotalPrice(totalPrice);
    }
  };

  const addToFavorites = async () => {
    if (selectedFood) {
      try {
        const response = await axios.post('http://localhost:5000/add-to-favorites', { foodId: selectedFood._id });
        console.log('Added to favorites:', response.data);
        setIsFavorite(true); 
      } catch (error) {
        console.error('Error adding to favorites:', error);
      }
    }
  };

  return (
    <div className="bg-dark text-light p-4">
      <h1>Categories</h1>
      <Row>
        {categories.map(category => (
          <Col key={category._id} xs={4} md={2} className="mb-3">
            <div className="bg-secondary text-light p-3 mb-2">
              <h2>{category.category} ({category.count})</h2>
            </div>
          </Col>
        ))}
      </Row>

      <Row>
        {foods.map(food => (
          <Col key={food._id} xs={6} md={4} lg={3} className="mb-4">
            <Card className="h-100">
              <Card.Img variant="top" src={food.image} className="card-img-top" />
              <Card.Body className="d-flex flex-column">
                <Card.Title>{food.name}</Card.Title>
                <Button variant="primary" onClick={() => handleModalOpen(food)} className="mt-auto">View Details</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={modalShow} onHide={() => setModalShow(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedFood && selectedFood.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="d-flex justify-content-between align-items-center mb-3">
            <img src={selectedFood && selectedFood.image} alt={selectedFood && selectedFood.name} className="w-50" />
            <Button variant="light" onClick={addToFavorites}>
              {isFavorite ? <i className="fas fa-heart" style={{ color: 'red' }}></i> : <i className="far fa-heart"></i>}
            </Button>
          </div>
          <p><strong>Price:</strong> ${selectedOption ? selectedOption.price : (selectedFood && selectedFood.price)}</p>
          <p>
            <strong>Quantity:</strong> 
            <DropdownButton id="dropdown-quantity" title={quantity}>
              {[...Array(10).keys()].map((value, index) => (
                <Dropdown.Item key={index} onClick={() => handleQuantityChange(value + 1)}>{value + 1}</Dropdown.Item>
              ))}
            </DropdownButton>
          </p>
          {selectedFood && selectedFood.options && selectedFood.options.length > 0 && (
            <p>
              <strong>Options:</strong>
              <DropdownButton id="dropdown-options" title="Select Option">
                {selectedFood.options.map((option, index) => (
                  <Dropdown.Item key={index} onClick={() => handleOptionSelect(option)}>{option.name} - ${option.price}</Dropdown.Item>
                ))}
              </DropdownButton>
            </p>
          )}
          <p><strong>Total Price:</strong> ${totalPrice}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setModalShow(false)}>Close</Button>
          <Button variant="primary" onClick={handleAddToCart}>Add to Cart</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
