import React, { useState } from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'; // Import Navigate
import CustomNavbar from './components/Navbar';
import Home from './components/Home';
import Orders from './components/Orders';
import Cart from './components/Cart';
import Login from './components/Login';
import Signup from './components/Signup';
import AdminLoginForm from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import FoodList from './components/FoodList';
import UserActivity from './components/UserActivity';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import AdminRegister from './components/AdminRegister';
import { CartProvider } from './components/Contextreducer';

function App() {
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleUserLogout = () => {
    localStorage.removeItem('userEmail');
    localStorage.removeItem('Name');
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setIsAdminLoggedIn(false);
  };

  const handleAdminLogout = () => {
    localStorage.removeItem('adminToken');
    setIsAdminLoggedIn(false);
  };

  return (
    <CartProvider>
      <Router>
        <div className="App">
          <ToastContainer />
          <CustomNavbar isLoggedIn={isLoggedIn} isAdminLoggedIn={isAdminLoggedIn} onUserLogout={handleUserLogout} onAdminLogout={handleAdminLogout} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/orders" element={<Orders />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/food-list" element={<FoodList />} />
            <Route path="/user-activity" element={<UserActivity />} />
            <Route path="/admin-login" element={<AdminLoginForm onLogin={() => setIsAdminLoggedIn(true)} />} />
            <Route path="/admin-register" element={<AdminRegister />} />
            <Route path="/login" element={<Login onLogin={() => setIsLoggedIn(true)} />} />
            <Route path="/signup" element={<Signup />} />
            {isLoggedIn && isAdminLoggedIn && <Route path="/admin-dashboard" element={<AdminDashboard />} />}
            {isLoggedIn && <Route path="/admin-dashboard" element={<Navigate to="/" />} />}
            {!isLoggedIn && <Route path="/admin-dashboard" element={<Navigate to="/login" />} />}
          </Routes>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
