import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Row, Col, Button, DropdownButton, Dropdown, Modal } from 'react-bootstrap';
import Cart from './Cart'; // Assuming Cart component is located in './Cart'

export default function Home() {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cart, setCart] = useState([]);
  const [selectedFood, setSelectedFood] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [modalShow, setModalShow] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    async function fetchData() {
      try {
        const foodResponse = await axios.get('http://localhost:5000/foods');
        setFoods(foodResponse.data);

        const categoryResponse = await axios.get('http://localhost:5000/category');
        setCategories(categoryResponse.data.map(category => ({ ...category, count: foods.filter(food => food.category === category.category).length })));
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, [foods]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = () => {
    if (selectedFood) {
      const price = selectedFood.price * quantity;
      const cartItem = { name: selectedFood.name, quantity, price, options: selectedFood.options };
      setCart([...cart, cartItem]);
      setModalShow(false);
    }
  };

  const handleModalOpen = (food) => {
    setSelectedFood(food);
    setModalShow(true);
    setQuantity(1); // Reset quantity to 1 when opening the modal
    calculateTotalPrice(food.price, 1); // Calculate total price initially with quantity 1
  };

  const calculateTotalPrice = (price, quantity) => {
    setTotalPrice(price * quantity);
  };

  const handleQuantityChange = (value) => {
    setQuantity(value);
    calculateTotalPrice(selectedFood.price, value);
  };

  const updateCartItem = (index, quantity) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity = quantity;
    updatedCart[index].price = selectedFood.price * quantity;
    setCart(updatedCart);
    calculateTotalPrice(selectedFood.price, quantity);
  };

  const removeCartItem = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
  };

  return (
    <div className="bg-dark text-light p-4">
      <h1>Categories</h1>
      <Row>
        {categories.map(category => (
          <Col key={category._id} xs={2} md={2} className="mb-3" onClick={() => handleCategoryChange(category.category)}>
            <div className="bg-secondary text-light p-3 mb-2">
              <h2>{category.category} ({category.count})</h2>
            </div>
          </Col>
        ))}
      </Row>

      <Row>
        {foods.map(food => (
          <Col key={food._id} xs={12} md={4} className="mb-3">
            <Card className="bg-secondary text-light" onClick={() => handleModalOpen(food)}>
              <Card.Img variant="top" src={food.image} />
              <Card.Body>
                <Card.Title>{food.name}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={modalShow} onHide={() => setModalShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedFood && selectedFood.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={selectedFood && selectedFood.image} alt={selectedFood && selectedFood.name} style={{ width: '100%', marginBottom: '10px' }} />
          <p><strong>Price:</strong> ${selectedFood && selectedFood.price}</p>
          <p>
            <strong>Quantity:</strong> 
            <DropdownButton id="dropdown-quantity" title={quantity}>
              {[...Array(10).keys()].map((value, index) => (
                <Dropdown.Item key={index} onClick={() => handleQuantityChange(value + 1)}>{value + 1}</Dropdown.Item>
              ))}
            </DropdownButton>
          </p>
          <p><strong>Total Price:</strong> ${totalPrice}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setModalShow(false)}>Close</Button>
          <Button variant="primary" onClick={handleAddToCart}>Add to Cart</Button>
        </Modal.Footer>
      </Modal>

      <Cart cart={cart} updateCartItem={updateCartItem} removeCartItem={removeCartItem} />
    </div>
  );
}
