import React, { createContext, useContext, useState } from 'react';

// Create the UserContext
const UserContext = createContext();

// Create a custom hook to use the user data or functionality
export const useUser = () => useContext(UserContext);

// Create the UserProvider component to wrap your application
export const UserProvider = ({ children }) => {
  // Define state to manage user data or functionality
  const [user, setUser] = useState(null);

  // Return the UserContext.Provider with the value of user and setUser
  return (
    <UserContext.Provider value={{ user, setUser }}>
      {children}
    </UserContext.Provider>
  );
};
