import React, { useState } from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CustomNavbar from './components/Navbar';
import Home from './components/Home';
import Orders from './components/Orders';
import Cart from './components/Cart';
import Login from './components/Login';
import Signup from './components/Signup';
import AdminLoginForm from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import FoodList from './components/FoodList';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import AdminRegister from './components/AdminRegister';
import { CartProvider } from './components/Contextreducer';

// Custom route component to handle authentication checks
const PrivateRoute = ({ component: Component, isAuthenticated, ...rest }) => (
  <Route {...rest} element={isAuthenticated ? <Component /> : <Navigate to="/login" />} />
);

function App() {
  const [isAdminLoggedIn, setIsAdminLoggedIn] = useState(false);

  return (
    <CartProvider>
      <Router>
        <div className="App">
          <ToastContainer />
          <CustomNavbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <PrivateRoute path="/orders" component={Orders} isAuthenticated={!isAdminLoggedIn} />
            <PrivateRoute path="/cart" component={Cart} isAuthenticated={!isAdminLoggedIn} />
            <PrivateRoute path="/food-list" component={FoodList} isAuthenticated={isAdminLoggedIn} />
            <PrivateRoute path="/admin-dashboard" component={AdminDashboard} isAuthenticated={isAdminLoggedIn} />
            <PrivateRoute path="/user-activity" component={UserActivity} isAuthenticated={isAdminLoggedIn} />
            <Route
              path="/admin-login"
              element={<AdminLoginForm onLogin={() => setIsAdminLoggedIn(true)} />}
            />
            <Route path="/admin-register" element={<AdminRegister />} />
          </Routes>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
