// Cart.js
import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import { useCart, useDispatchCart } from './Contextreducer';

export default function Cart() {
  const cart = useCart();
  const dispatch = useDispatchCart();
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    // Calculate total price when cart items change
    let totalPrice = 0;
    if (cart) {
      cart.forEach(item => {
        totalPrice += item.price;
      });
    }
    setTotalPrice(totalPrice);
  }, [cart]);

  // Function to handle quantity change for a cart item
  const handleQuantityChange = (index, quantity) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity = quantity;
    updatedCart[index].price = updatedCart[index].unitPrice * quantity;
    dispatch({ type: 'UPDATE_CART', payload: updatedCart });
  };
  

  // Function to handle removal of a cart item
  const handleRemoveItem = (index) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: index });
  };

  return (
    <div>
      <h1>Cart</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cart && cart.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>${item.price}</td>
              <td>
                <input
                  type="number"
                  value={item.quantity}
                  onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                  min="1"
                />
              </td>
              <td>
                <Button variant="danger" onClick={() => handleRemoveItem(index)}>Remove</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h2>Total Price: ${totalPrice}</h2>
    </div>
  );
}
