import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Modal, DropdownButton, Dropdown, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useDispatchCart } from './Contextreducer';

export default function MyProfile() {
  const [user, setUser] = useState(null);
  const [ordersCount, setOrdersCount] = useState(0);
  const [selectedFood, setSelectedFood] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedOption, setSelectedOption] = useState(null);
  const [modalShow, setModalShow] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [profileImage, setProfileImage] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatchCart();

  useEffect(() => {
    async function fetchProfileData() {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/user-profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setUser(response.data.user);
        setOrdersCount(response.data.ordersCount);
        setProfileImage(response.data.user.profileImage);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    }

    fetchProfileData();
  }, []);

  const handleUpdateProfilePicture = async (e) => {
    const formData = new FormData();
    formData.append('profileImage', e.target.files[0]);
    
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/update-profile-picture', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data.message);
      setUser({ ...user, profileImage: response.data.profileImage });
    } catch (error) {
      console.error('Error updating profile picture:', error);
    }
  };

  const handleModalOpen = (food) => {
    setSelectedFood(food);
    setModalShow(true);
    setQuantity(1);
    setSelectedOption(null);
    setTotalPrice(food.price);
  };

  const handleQuantityChange = (value) => {
    setQuantity(value);
    const totalPrice = selectedOption ? selectedOption.price * value : selectedFood.price * value;
    setTotalPrice(totalPrice);
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    if (option) {
      const totalPrice = option.price * quantity;
      setTotalPrice(totalPrice);
    }
  };

  const handleAddToCart = () => {
    if (selectedFood) {
      const price = selectedOption ? selectedOption.price : selectedFood.price;
      const totalPrice = price * quantity;
      const cartItem = { name: selectedFood.name, quantity, price: totalPrice, option: selectedOption };
      dispatch({ type: 'ADD_TO_CART', payload: cartItem });
      setModalShow(false);
      navigate('/cart');
    }
  };

  return (
    <div>
      <h1>My Profile</h1>
      {user ? (
        <div>
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src={profileImage} />
            <Card.Body>
              <Card.Title>{user.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">{user.email}</Card.Subtitle>
              <Form.Group>
                <Form.Label>Update Profile Picture</Form.Label>
                <Form.Control type="file" onChange={handleUpdateProfilePicture} />
              </Form.Group>
              <Card.Text>Orders made: {ordersCount}</Card.Text>
            </Card.Body>
          </Card>

          <Modal show={modalShow} onHide={() => setModalShow(false)}>
            <Modal.Header closeButton>
              <Modal.Title>{selectedFood && selectedFood.name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <img src={selectedFood && selectedFood.image} alt={selectedFood && selectedFood.name} style={{ width: '100%', marginBottom: '10px' }} />
              <p><strong>Price:</strong> ${selectedOption ? selectedOption.price : (selectedFood && selectedFood.price)}</p>
              <p>
                <strong>Quantity:</strong> 
                <DropdownButton id="dropdown-quantity" title={quantity}>
                  {[...Array(10).keys()].map((value, index) => (
                    <Dropdown.Item key={index} onClick={() => handleQuantityChange(value + 1)}>{value + 1}</Dropdown.Item>
                  ))}
                </DropdownButton>
              </p>
              {selectedFood && selectedFood.options && selectedFood.options.length > 0 && (
                <p>
                  <strong>Options:</strong>
                  <DropdownButton id="dropdown-options" title="Select Option">
                    {selectedFood.options.map((option, index) => (
                      <Dropdown.Item key={index} onClick={() => handleOptionSelect(option)}>{option.name} - ${option.price}</Dropdown.Item>
                    ))}
                  </DropdownButton>
                </p>
              )}
              <p><strong>Total Price:</strong> ${totalPrice}</p>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setModalShow(false)}>Close</Button>
              <Button variant="primary" onClick={handleAddToCart}>Add to Cart</Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
